import streamlit as st

st.title("Coffie Test Poll")

col1,col2 = st.columns(2)

with col1:
    st.header("Dark Coddfe")
    st.image("data:image/jpeg;base64,")
    vote1 = st.button("Vote Dark Coffee")

with col2:
    st.header("Cold Coddfe")
    st.image("data:image/jpeg;base64,",width=200,)
    vote2 = st.button("Vote Cold Cofee")

if vote1:
    st.success("Thanks for Vote Dark Coffee")

if vote2:
    st.success("Thanks for Vote Cold Coffee")